import * as React from 'react';
import { useState, useEffect } from 'react';
import { io } from 'socket.io-client';

import { Navbar, Container, Nav, NavDropdown, Image } from 'react-bootstrap';

import { logout, useGetAccountInfo } from '@elrondnetwork/dapp-core';
import { Link, useNavigate } from 'react-router-dom';
import Logo from 'assets/Logo';

import styles from './styles.module.scss';
import { backend } from 'config';

const NavbarComponent = () => {
  const [socket, setSocket] = useState(null);
  const [account, setAccount] = useState('');
  let navigate = useNavigate();
  const { address } = useGetAccountInfo();

  useEffect(() => {
    const s = io(backend);
    setSocket(s);

    return () => {
      s.disconnect();
    }
  }, [])

  useEffect(() => {
    if(socket === null) return;
      else {
        socket.emit('load-account', address)
        socket.on('get-account', (data) => {
          if(data.message === 'OK') {
            setAccount(data.account.username)
          } else {
            setAccount(address.substr(0, 8) + '...' + address.substr(address.length - 8))
          }
        })
      }
  }, [socket])

  const buttons = [
    {
      label: 'Race',
      link: '/race'
    },
    {
      label: 'History',
      link: '/raceHistory'
    },
    {
      label: 'Stable',
      link: '/stable'
    },
    {
      label: 'Tournament',
      link: '/tournament'
    },
    {
      label: 'Leaderboard',
      link: '/leaderboard'
    }
  ];

  return (
    <Navbar expand="md" variant='light'>
      <Container fluid>
      <Navbar.Brand>
        <Link to='/' className='text-light text-decoration-none d-flex align-items-center'>
          <Logo width={50} height={50} />
          <span className={styles.title + ' ml-3'}>Estar</span>
      </Link>
      </Navbar.Brand>
      <Navbar.Toggle aria-controls="navbarScroll" />
      <Navbar.Collapse id="navbarScroll" className="justify-content-end">

      <Nav
        className="me-auto my-2 my-lg-0 align-items-center"
        style={{ maxHeight: '100px' }}
        navbarScroll
      >
      {buttons.map((button) => (
          <Link
            key={button.label}
            to={button.link}
            className='mr-3 text-light text-decoration-none'
            style={{ fontSize: '16.5px' }}
          >
            {button.label}
          </Link>
        ))}
      <NavDropdown title={
        <Image src={'https://randomuser.me/api/portraits/men/59.jpg'} height='40px' roundedCircle/>
      }>
        <NavDropdown.Item>
          <Link to="/account" className="text-decoration-none">
            Account
          </Link>
        </NavDropdown.Item>
        <NavDropdown.Item>
          <span onClick={() => logout()}>Log out</span>
        </NavDropdown.Item>
      </NavDropdown>
    </Nav>
    </Navbar.Collapse>
    </Container>
    </Navbar>
  );
};

export default NavbarComponent;
