import React from 'react';
import { useState, useEffect } from 'react';
import { useGetAccountInfo } from '@elrondnetwork/dapp-core';
import { getNftsWallet } from '../../apiRequests';
import styles from './styles.module.scss';

const Nfts = (props: any) => {
  const { address } = useGetAccountInfo();
  const [nft, setNft] = useState([]);
  useEffect(() => {
    const fetch = async () => {
      const nfts = await getNftsWallet(address).then((result) => result.data);
      setNft(nfts);
    };
    fetch();
  }, []);

  return (
    <div
      className={styles.nftGrid}
      style={
        props.col ? {
            gridTemplateColumns: `repeat(${props.col}, 1fr)`
        } : {
          gridTemplateColumns: 'repeat(4, 1fr)' }}
    >
      {nft ? (
        nft.map(({ identifier, url, name }) => (
          <div key={identifier} className={styles.nftContent}>
            <img src={url} className={styles.img} />
            <p>{name}</p>
          </div>
        ))
      ) : (
        <p>You don&apos;t have EquiStar Nfts.</p>
      )}
    </div>
  );
};

export default Nfts;
