import React from 'react';
import { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useGetAccountInfo } from '@elrondnetwork/dapp-core';
import races from '../../components/Races';
import Button from '../../components/Button';
import { tokenName } from 'config';
import { io } from 'socket.io-client';
import Slots from './Components/Slots';

import styles from './styles.module.scss';

const Race = () => {
  const { address } = useGetAccountInfo();
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();
  const handleRedirect = () =>
    Boolean(address) ? setLoading(false) : navigate('/unlock');

  useEffect(handleRedirect, [address]);
  return (
    <div className='container'>
      <div className='row mt-4'>
        {races.map((race) => (
          <div key={race.id} className='col-12 col-md-4 mb-4 px-lg-4'>
            <div className={styles.card}>
              <div className={styles.cardContent}>
                <h3 className={styles.raceName}>
                  {race.name} #{race.id}
                </h3>
                <h5 className={styles.raceDescription}>
                  {race.description}
                </h5>
                <h3 className={styles.racePlayers}>Players</h3>
                <h5 className={styles.racePlayersNB}>
                  7<span className={styles.racePlayersNBSlash}>/</span>8
                </h5>
                <h3 className={styles.racePrize}>Race prize</h3>
                <div className={styles.racePrizeContent}>
                  <h6 className={styles.racePrizeItem}>First</h6>
                  <h6 className={styles.racePrizeItem}>6.9$ESTAR</h6>
                </div>
                <Button race={race} />
              </div>
            </div>
          </div>
        ))};
      </div>
    </div>
  );
};

export default Race;
