import axios from 'axios';

const fetchNfts = (url: string) =>
  async function getNfts() {
    try {
      const { data } = await axios.get('https://api.elrond.com/nfts?size=10000&collection=EQUISTAR-3f393f', {
        params: {
          url: address,
          receiver: contractAddress,
          condition: 'must',
          size: 25
        },
        timeout
      });

      return {
        data: data,
        success: data !== undefined
      };
    } catch (err) {
      return {
        success: false
      };
    }
  };

export const getNfts = fetchNfts('/transactions');
