import React, { useState, useEffect } from 'react'
import { Form } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import Button from 'react-bootstrap/Button';

const EditAccountForm = ({ socket, account }) => {

  const navigate = useNavigate();
  const [message, setMessage] = useState(null);
  const [username, setUsername] = useState(null);
  const [accountInfo, setAccountInfo] = useState();
  const address = account.address;

  useEffect(() => {
    if(socket == null ) return;
    socket.on('get-account', (data) => {
      if(data.message === 'OK') {
        setAccount(data.account);
        setExist(true);
      } else if(data.message === 'NULL') {
        setExist(false);
      }
      setWait(false);
    });
  }, [socket])

  const editAccount = async () => {
    if(username === null) {
      setMessage('Please enter an username!')
    } else {
      if(username !== account.username) {
        await socket.emit('edit-account', {address, username});
        setMessage('Success!')
        navigate('/account');
      } else {
        setMessage('Username is the same!')
      }
    }
  }

  return (
    <Form className="text-light mx-auto">
      <Form.Group className="mb-3" controlId="formBasicEmail">
        <Form.Label>Username</Form.Label>
        <Form.Control
          type="text"
          placeholder="Enter username"
          onChange={(e) => setUsername(e.target.value)}
        />
        {message !== null ?
          message === 'Success!' ? (
            <Form.Text className="text-success">
              {message}
            </Form.Text>
          ) : (
            <Form.Text className="text-danger">
              {message}
            </Form.Text>
          )

         : (
          <Form.Text className="text-muted">
            We'll never share your email with anyone else.
          </Form.Text>
        )}
      </Form.Group>
     <Button
       variant="primary"
       className="d-block mx-auto"
       onClick={editAccount}
     >
        Save changes
      </Button>
    </Form>
  );
}

export default EditAccountForm;
