import React from 'react'
import EditProfile from '../Edit';
import { useNavigate } from 'react-router-dom';
import Image from 'react-bootstrap/Image'

const AccountInfo = ({ account }) => {
  const navigate = useNavigate();
  return (
      <div className="container">
        <div className="row mt-4">
          <div className="col-3 mx-auto text-light"> 
            <Image src='https://www.pngfind.com/pngs/m/540-5404861_horse-riding-clipart-animated-spirit-horse-riding-free.png' roundedCircle fluid={true} />
          </div>
        </div>
        <div className="row mt-4">
          <div className="col-12 text-center text-light"> 
            <h1>Hello, {account.username}!</h1>
          </div>
        </div>
        <div className="row">
          <div className="col-12">
            <button
              className="btn btn-primary mx-auto d-block"
              onClick={() => navigate('/account/edit')}
            >
              Edit profile
            </button>
          </div>
        </div>
      </div>
  );
}

export default AccountInfo;
