import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useGetAccountInfo } from '@elrondnetwork/dapp-core';

function ViewTrack() {
  const [entry, setEntry] = useState([]);
  const { address } = useGetAccountInfo();

  useEffect(() => {
    fetchEntry();
  }, []);

  const fetchEntry = () => {
    axios
      .get('http://localhost:4000/race/track/all')
      .then((res) => {
        res.data.forEach((item) => {
          if (item.address == address) {
            setEntry(item);
          }
        });
      })
      .catch((err) => setEntry(err));
  };

  console.log(entry);
  return (
    <div>
      <h1>View</h1>
      <div>
        <div>
          {entry ? (
            entry.map((item) => {
              return (
                <div key={item._id}>
                  <h1>{item.horse}</h1>
                </div>
              );
            })
          ) : (
            <h1>Nu</h1>
          )}
        </div>
      </div>
    </div>
  );
}

export default ViewTrack;
