import axios from 'axios';

const api = 'https://api.elrond.com';

const http = axios.create({
  baseURL: api,
  headers: { 'Content-Type': 'application/json'}
});

const collection = 'EQUISTAR-3f393f';

export const getNfts = async (wallet) => {
  try {
    const { data } = await http.get('/accounts/' + wallet + '/nfts?collection=' + collection);
    return {
      data: data,
      success: true
    }
  } catch (error) {
    return {
      data: error.response.data,
      success: false
    }
  }
}

export const NbNftsMint = async (wallet) => {
  try {
    const { data } = await http.get('/accounts/' + wallet + '/nfts?collection=' + collection);
    return {
      data: data,
      success: true
    }
  } catch (error) {
    return {
      data: error.response.data,
      success: false
    }
  }
}
