import React, { useState, useEffect } from 'react';
import {
  getNftsWallet,
  getSlots,
  checkExistRace,
  updatePlayers
} from '../../apiRequests';
import { useGetAccountInfo } from '@elrondnetwork/dapp-core';
import styles from './styles.module.scss';

const SelectHorse = (props: any) => {
  const { address } = useGetAccountInfo();
  const [exist, setExist] = useState(false);
  const [nft, setNft] = useState([]);
  const bonus = ['SPEED', 'EDURANCE', 'AGILITY'];
  const [slots, setSlots] = useState(0);
  useEffect(() => {
    const fetch = async () => {
      const data = await getNftsWallet(address).then((res) => res.data);
      setNft(data);
      const nbSlots = await getSlots(props.id).then((res) => res.data);
      setSlots(nbSlots);
      const check = await checkExistRace(props.id).then((res) => res.exist);
      setExist(check);
    };
    fetch();
  }, []);

  const create = async (horse: string) => {
    if (exist && slots < 8) {
      if(slots == 7) {
        console.log('The game started');
      } else {
        console.log('Slots');
      console.log(horse);
      await updatePlayers(props.id, address, horse);
      }
    }
  };

  return (
    <div
      className={styles.nftGrid}
      style={
        props.col
          ? {
              gridTemplateColumns: `repeat(${props.col}, 1fr)`
            }
          : {
              gridTemplateColumns: 'repeat(4, 1fr)'
            }
      }
    >
      {nft ? (
        nft.map(({ identifier, name, url }) => (
          <div
            key={identifier}
            className={styles.nftContent}
            onClick={() => create(name)}
          >
            <img src={url} className={styles.img} />
            <p
              style={props.color ? { color: props.color } : { color: 'white' }}
            >
              {name}
            </p>
          </div>
        ))
      ) : (
        <p>You don&apos;t have EquiStar Nfts.</p>
      )}
    </div>
  );
};

export default SelectHorse;
