import React, { useEffect, useState } from 'react';
import { useGetAccountInfo } from '@elrondnetwork/dapp-core';
import { faSpinner } from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { useNavigate, Link } from 'react-router-dom';
import CarouselComponent from 'components/Carousel';
import Rewards from 'components/Rewards';
import Cards from '../../components/Cards';
import Nfts from '../../components/Nfts';
import { Alert } from 'react-bootstrap';
import { io } from 'socket.io-client';
import { backend } from 'config';

const Dashboard = () => {
  const { address } = useGetAccountInfo();
  const [loading, setLoading] = useState(true);
  const [socket, setSocket] = useState(null);
  const [haveAccount, setHaveAccount] = useState(false);
  const navigate = useNavigate();
  const handleRedirect = () =>
    Boolean(address) ? setLoading(false) : navigate('/unlock');

  useEffect(handleRedirect, [address]);

  useEffect(() => {
    const s = io(backend);
    setSocket(s);

    return () => {
      s.disconnect;
    }
  }, []);

  useEffect(() => {
    if(socket == null ) return;
    socket.emit('load-account', address);
  }, [socket])

  useEffect(() => {
    if(socket == null ) return;
    socket.on('get-account', (data) => {
      if(data.message === 'OK') {
        setHaveAccount(true);
      }
    });
  }, [socket])

  if (loading) {
    return (
      <div
        style={{ fontSize: '30px' }}
        className='d-flex align-items-center justify-content-center text-white flex-fill'
      >
        <FontAwesomeIcon
          icon={faSpinner}
          size='2x'
          spin={true}
          className='mr-3'
        />
        Loading...
      </div>
    );
  }

  return (
    <div className='container'>
      { haveAccount ? <Alert variant='primary'>
        You do not have an account created, <Link to='/account/create'>click here</Link>!
      </Alert> : ('sall') }
      <div className="row mb-3">
        <div className="col-12 col-md-6 d-md-block mx-auto">
          <CarouselComponent />
        </div>
      </div>
      <div className="row">
        <div className="col-12">
          <Cards />
        </div>
      </div>
      <div className="row mb-5">
        <div className="col-12 col-md-6 d-md-block mx-auto">
          <Rewards address={address} />
        </div>
      </div>
      <div className="row mb-5">
        <div className="col-12">
          <h1 className="text-center text-light mb-4">My NFTs</h1>
          <Nfts withDetails={true} />
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
