import React, { useState } from 'react'
import {
  faDoorOpen
} from '@fortawesome/free-solid-svg-icons';
import Modal from 'react-bootstrap/Modal';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import Enter from './Enter';

const TournamentModel = ({ address, socket }) => {
  const [show, setShow] = useState(false);
  const [loading, setLoading] = useState(true);
  const [nft, setNft] = useState(null);
  const [message, setMessage] = useState('');

  // Loading

  function setDelay(ms) {
    return new Promise((resolve, reject) => {
      setTimeout(resolve, ms);
    })
  }

  async function makeLoading() {
    await setDelay(1000);
    setLoading(false);
  }
  useEffect(() => {
    makeLoading();
  }, []);

  useEffect(() => {
    const handler = (nfts) => {
      setNft(nfts);
    };

    socket.emit('get-status', address);
    socket.on('recive-status', handler);
  }, [])

  return (
    <>
      <button
        className="btn btn-primary"
        onClick={() => setShow(true)}
      >
        Join <FontAwesomeIcon icon={faDoorOpen} />
      </button>
      <Modal
        show={show}
        backdrop="static"
        size="lg"
        keyboard={false}
      >
        <Modal.Header>
          <Modal.Title>Select your horse</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Enter socket={socket} address={address} />
        </Modal.Body>
      </Modal>
    </>
  )
}

export default TournamentModel;
