import React, { useState } from 'react'
import { useNavigate } from 'react-router-dom';
import { Modal } from 'react-bootstrap';

const EnterRace = ({ showSelect, nft, race, address, estar, egld, socket }) => {
  const navigate = useNavigate();
  const [show, setShow] = useState(showSelect);
  const [success, setSuccess] = useState(false);
  const [message, setMessage] = useState(null);
  const [raceId, setRaceId] = useState('');

  function setDelay(ms) {
    return new Promise((resolve, reject) => {
      setTimeout(resolve, ms);
    })
  }

  const enter = async (horse, url) => {
    await setDelay(2000);
    const data = await {
        address: address,
        horse: horse,
        horseUrl: url,
        feePaid: true,
        entryFee: race.withEstar ? estar : egld,
        with: race.withEstar ? 'ESTAR' : 'EGLD',
        raceId: race.id,
      };
    socket.emit('enter-race', data);
    const handle = (response) => {
        setSuccess(true);
        setShow(response.showb);
        setMessage(response.message);
        setRaceId(response.id);
      }

      socket.on('recive-response', handle);
  };

  return (
    <>
      <Modal
        show={show}
        size='xl'
        backdrop='static'
        keyboard={false}
        onHide={() => setShow(false)}
        aria-labelledby='contained-modal-title-vcenter'
        centered
      >
        <Modal.Header className='bg-white'>
          <Modal.Title id='contained-modal-title-vcenter'>
            Select Horse
          </Modal.Title>
        </Modal.Header>
        <Modal.Body className='bg-white px-0'>
            <div className='row'>
              {nft ? (
                nft.map(({ fileUri, name, stamina, inRace }) => {
                      if(race.id != 1) {
                        if(!inRace) {
                          return (
                          <div
                            key={name}
                            className='col-12 col-md-4 text-center'
                            onClick={() => {
                             enter(name, fileUri);
                            }}
                          >
                            <img src={fileUri} className='d-block mx-auto' height='250px' />
                            <p style={{ color: 'black' }}>{name}</p>
                          </div>
                        );
                        }
                      } else {
                        return (
                          <div
                            key={name}
                            className='col-12 col-md-4 text-center'
                            onClick={() => {
                             enter(name, fileUri);
                            }}
                          >
                            <img src={fileUri} className='d-block mx-auto' height='250px' />
                            <p style={{ color: 'black' }}>{name}</p>
                            {race.id > 1 ? '' : stamina == 25 && <p className='text-danger'>Stamina: {stamina}</p> ||
                              stamina == 50 && <p className='text-warning'>Stamina: {stamina}</p> ||
                              stamina > 50 && <p className='text-primary'>Stamina: {stamina}</p>
                            }
                          </div>
                        );
                      }
                    })
              ) : (
                <p>You don&apos;t have EquiStar Nfts.</p>
              )}
            </div>
        </Modal.Body>
      </Modal>
      <Modal
        show={success}
        backdrop='static'
        keyboard={false}
        size='md-down'
        onHide={() => setSuccess(false)}
        aria-labelledby='contained-modal-title-vcenter'
        centered
      >
        <Modal.Header className='bg-white'>
          <Modal.Title id='contained-modal-title-vcenter'>
            ðŸŽ‰ Congratulations!
          </Modal.Title>
        </Modal.Header>
        <Modal.Body className='bg-white px-3'>
            <h4>{message}</h4>
        </Modal.Body>
        <Modal.Footer className='bg-white'>
            <button
              className='btn btn-primary d-block mx-auto'
              onClick={() => navigate(`/race/${raceId}`)}
            >
              Close
            </button>
        </Modal.Footer>
      </Modal>
    </>
  )
}

export default EnterRace
