import axios from 'axios';

const api = 'https://api.elrond.com';
const apiBk = 'http://localhost:4000/backend';
const http = axios.create({
  baseURL: api,
  headers: {
    'Content-type': 'application/json'
  }
});
const http2 = axios.create({
  baseURL: apiBk,
  headers: {
    'Content-type': 'application/json'
  }
});
const collection = 'EQUISTAR-3f393f';
const token = 'ESTAR-afaaf0';

export const NbNftsMint = async () => {
  try {
    const { data } = await http.get('/nfts/count?collection=' + collection);
    return {
      data: data,
      success: true
    };
  } catch (err) {
    return {
      data: err.response.data,
      success: false
    };
  }
};

export const fetchEstarWallet = async (address) => {
  try {
    const { data } = await http.get(
      '/accounts/' + address + '/tokens/' + token
    );
    return {
      data: Number(data.balance) / 100,
      success: true
    };
  } catch (err) {
    return {
      data: err.response.data,
      success: false
    };
  }
};

export const getNftsWallet = async (address) => {
  try {
    const { data } = await http.get(
      '/accounts/' + address + '/nfts?collection=' + collection
    );
    return {
      data: data,
      success: true
    };
  } catch (err) {
    return {
      data: err.response.data,
      success: false
    };
  }
};

export const getSlots = async (id) => {
  try {
    const { data } = await http2.get('/race/' + id);
    let k;
    for (let i = 0; i < data.length; i++) {
      k = data[i].player.length;
    }
    return {
      data: Number(k),
      success: true
    };
  } catch (err) {
    return {
      data: err.response.data,
      success: false
    };
  }
};
