import * as React from 'react';
import { useState, useEffect } from 'react';
import { io } from 'socket.io-client';

import { Navbar, Container, Nav } from 'react-bootstrap';

import { logout, useGetAccountInfo } from '@elrondnetwork/dapp-core';
import {
  faPowerOff,
  faHorse,
  faBook,
  faUser,
  faList
} from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { Link, useNavigate } from 'react-router-dom';
import Logo from 'assets/Logo';

import modifiable from 'helpers/modifiable';
import styles from './styles.module.scss';
import { backend } from 'config';

const NavbarComponent = () => {
  const [socket, setSocket] = useState(null);
  const [account, setAccount] = useState('');
  let navigate = useNavigate();
  const routeChange = (path) => {
    navigate(path);
  };
  const { address } = useGetAccountInfo();

  useEffect(() => {
    const s = io(backend);
    setSocket(s);

    return () => {
      s.disconnect();
    }
  }, [])

  useEffect(() => {
    if(socket === null) return;
      else {
        socket.emit('load-account', address)
        socket.on('get-account', (data) => {
          if(data.message === 'OK') {
            setAccount(data.account.username)
          } else {
            setAccount(address.substr(0, 8) + '...' + address.substr(address.length - 8))
          }
        })
      }
  })

  const buttons = [
    {
      icon: <FontAwesomeIcon icon={faUser} />,
      label: account,
      onClick: () => routeChange('/account')
    },
    {
      icon: <FontAwesomeIcon icon={faHorse} />,
      label: 'Race',
      onClick: () => routeChange('/race')
    },
    {
      icon: <FontAwesomeIcon icon={faBook} />,
      label: 'History',
      onClick: () => routeChange('/raceHistory')
    },
    {
      icon: <FontAwesomeIcon icon={faList} />,
      label: 'Category',
      onClick: () => routeChange('/category')
    },
    {
      icon: <FontAwesomeIcon icon={faPowerOff} />,
      label: 'Disconnect',
      onClick: () => logout('/unlock')
    }
  ];

  return (
    <Navbar className={styles.nav} expand="md">
      <Container fluid>
      <Navbar.Brand>
        <Link to='/' className={styles.heading}>
          <Logo width={50} height={50} />
          <span className={styles.title + ' ml-3'}>Estar</span>
      </Link>
      </Navbar.Brand>
      <Navbar.Toggle aria-controls="navbarScroll" className='bg-white' />
      <Navbar.Collapse id="navbarScroll" className="justify-content-end">

      <Nav
        className="me-auto my-2 my-lg-0"
        style={{ maxHeight: '100px' }}
        navbarScroll
      >
      <div className={styles.buttons}>
        {buttons.map((button) => (
          <div
            key={button.label}
            className={modifiable(
              'button',
              [button.onClick && 'clickable'],
              styles
            )}
            onClick={button.onClick}
          >
            <div className={styles.icon}>{button.icon}</div>
            <span>{button.label}</span>
          </div>
        ))}
      </div>
    </Nav>
    </Navbar.Collapse>
    </Container>
    </Navbar>
  );
};

export default NavbarComponent;
