import axios from 'axios';

interface GetLatestNft {
  identifier: string;
  collection: string;
  name: string;
  url: string;
}

const fetchNfts = (url: string) =>
  async function getTransactions({
    identifier,
    collection,
    name,
    url
  }: GetLatestNft) {
    try {
      const { data } = await axios.get('https://api.elrond.com/nfts?size=10000&collection=EQUISTAR-3f393f', {
        params: {
          identifier: identifier,
          collection: collection,
          name: name,
          url: url
        },
      });

      return {
        data: data,
        success: data !== undefined
      };
    } catch (err) {
      return {
        success: false
      };
    }
  };

export const getTransactions = fetchTransactions('/transactions');
