import * as React from 'react';
import { useState, useEffect } from 'react';

import { Navbar, Container, Nav } from 'react-bootstrap';

import { logout, useGetAccountInfo } from '@elrondnetwork/dapp-core';
import {
  faPowerOff,
  faHorse,
  faStar,
  faCrown,
  faList
} from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { Link, useNavigate } from 'react-router-dom';
import Logo from 'assets/Logo';

import modifiable from 'helpers/modifiable';
import styles from './styles.module.scss';
import axios from 'axios';

const NavbarComponent = () => {
  let navigate = useNavigate();
  const routeChange = (path) => {
    navigate(path);
  };
  const { address } = useGetAccountInfo();
  const [heroTag, setHeroTag] = useState('');
  const fetchHeroTag = `https://api.elrond.com/accounts/${address}`;
  const getHeroTag = () => {
    axios.get(fetchHeroTag).then((res) => setHeroTag(res.data.username));
  };
  useEffect(() => getHeroTag(), []);
  const buttons = [
    {
      icon: '',
      label: heroTag ? `@${heroTag.replace('.elrond', '')}` : address.substr(0, 8) + '...' + address.substr(address.length - 8),
      onClick: () => navigator.clipboard.writeText(address)
    },
    {
      icon: <FontAwesomeIcon icon={faHorse} />,
      label: 'Race',
      onClick: () => routeChange('/race')
    },
    {
      icon: <FontAwesomeIcon icon={faStar} />,
      label: 'Stable',
      onClick: () => routeChange('/stable')
    },
    {
      icon: <FontAwesomeIcon icon={faCrown} />,
      label: 'Tournament',
      onClick: () => routeChange('/tournament')
    },
    {
      icon: <FontAwesomeIcon icon={faList} />,
      label: 'Leaderboard',
      onClick: () => routeChange('/leaderboard')
    },
    {
      icon: <FontAwesomeIcon icon={faPowerOff} />,
      label: 'Disconnect',
      onClick: () => logout('/unlock')
    }
  ];

  return (
    <Navbar bg="light" expand="lg">
  <Container fluid>
    <Navbar.Brand href="#">Navbar scroll</Navbar.Brand>
    <Navbar.Toggle aria-controls="navbarScroll" />
    <Navbar.Collapse id="navbarScroll">
      <Nav
        className="me-auto my-2 my-lg-0"
        style={{ maxHeight: '100px' }}
        navbarScroll
      >
        <Nav.Link href="#action1">Home</Nav.Link>
        <Nav.Link href="#action2">Link</Nav.Link>
        <NavDropdown title="Link" id="navbarScrollingDropdown">
          <NavDropdown.Item href="#action3">Action</NavDropdown.Item>
          <NavDropdown.Item href="#action4">Another action</NavDropdown.Item>
          <NavDropdown.Divider />
          <NavDropdown.Item href="#action5">
            Something else here
          </NavDropdown.Item>
        </NavDropdown>
        <Nav.Link href="#" disabled>
          Link
        </Nav.Link>
      </Nav>
      <Form className="d-flex">
        <FormControl
          type="search"
          placeholder="Search"
          className="me-2"
          aria-label="Search"
        />
        <Button variant="outline-success">Search</Button>
      </Form>
    </Navbar.Collapse>
  </Container>
</Navbar>
  );
};

export default NavbarComponent;
