import React, { useState, useEffect } from 'react'
import { useNavigate } from 'react-router-dom';
import Image from 'react-bootstrap/Image';

const AccountInfo = ({ account, socket }) => {
  const navigate = useNavigate();
  const [entries, setEntries] = useState(0);

  useEffect(() => {
    if(socket == null ) return;
    socket.emit('load-account', account.address);
  }, [socket])

  useEffect(() => {
    if(socket == null) return;
    socket.on('get-account-entries', entries => setEntries(entries));
  })

  return (
      <div className="container">
        <div className="row mt-4">
          <div className="col-3 mx-auto text-light"> 
            <Image src='https://www.pngfind.com/pngs/m/540-5404861_horse-riding-clipart-animated-spirit-horse-riding-free.png' roundedCircle fluid />
          </div>
        </div>
        <div className="row mt-4">
          <div className="col-12 text-center text-light"> 
            <h1>Hello, {account.username}!</h1>
          </div>
        </div>
        <div className="row">
          <div className="col-12">
            <button
              className="btn btn-primary mx-auto d-block"
              onClick={() => navigate('/account/edit')}
            >
              Edit profile
            </button>
          </div>
        </div>
        <div className="row text-center text-light">
         <div className="col-6">
            <h2>Statistics</h2>
            <h4>Races entries: {entries}</h4>
            <h4>Races entries: {entries}</h4>
         </div>
        </div>
      </div>
  );
}

export default AccountInfo;
