import axios from 'axios';

interface GetLatestNft {
  identifier: string;
  collection: string;
  name: string;
  url: string;
}

const fetchTransactions = (url: string) =>
  async function getTransactions({
    identifier,
    collection,
    name,
    url
  }: GetLatestNft) {
    try {
      const { data } = await axios.get(`${apiAddress}${url}`, {
        params: {
          indenti: identifier,
          receiver: contractAddress,
          condition: 'must',
          size: 25
        },
        timeout
      });

      return {
        data: data,
        success: data !== undefined
      };
    } catch (err) {
      return {
        success: false
      };
    }
  };

export const getTransactions = fetchTransactions('/transactions');
export const getTransactionsCount = fetchTransactions('/transactions/count');
