import * as React from 'react';
import { useState, useEffect } from 'react';
import { io } from 'socket.io-client';

import { Navbar, Container, Nav, NavDropdown } from 'react-bootstrap';

import { logout, useGetAccountInfo } from '@elrondnetwork/dapp-core';
import { Link, useNavigate } from 'react-router-dom';
import Logo from 'assets/Logo';

import styles from './styles.module.scss';
import { backend } from 'config';

const NavbarComponent = () => {
  const [socket, setSocket] = useState(null);
  const [account, setAccount] = useState('');
  let navigate = useNavigate();
  const { address } = useGetAccountInfo();

  useEffect(() => {
    const s = io(backend);
    setSocket(s);

    return () => {
      s.disconnect();
    }
  }, [])

  useEffect(() => {
    if(socket === null) return;
      else {
        socket.emit('load-account', address)
        socket.on('get-account', (data) => {
          if(data.message === 'OK') {
            setAccount(data.account.username)
          } else {
            setAccount(address.substr(0, 8) + '...' + address.substr(address.length - 8))
          }
        })
      }
  }, [socket])

  const buttons = [
    {
      label: 'Race',
      link: '/race'
    },
    {
      label: 'History',
      link: '/raceHistory'
    },
    {
      label: 'Stable',
      link: '/stable'
    },
    {
      label: 'Tournament',
      link: '/tournament'
    },
    {
      label: 'Leaderboard',
      link: '/leaderboard'
    }
  ];

  return (
    <Navbar expand="md">
      <Container fluid>
      <Navbar.Brand>
        <Link to='/' className={styles.heading}>
          <Logo width={50} height={50} />
          <span className={styles.title + ' ml-3'}>Estar</span>
      </Link>
      </Navbar.Brand>
      <Navbar.Toggle aria-controls="navbarScroll" className='bg-white' />
      <Navbar.Collapse id="navbarScroll" className="justify-content-end">

      <Nav
        className="me-auto my-2 my-lg-0"
        style={{ maxHeight: '100px' }}
        navbarScroll
      >
      {buttons.map((button) => (
          <Link
            key={button.label}
            to={button.link}
            className='mr-3 text-light text-decoration-none'
            style={{ fontSize: '16.5px' }}
          >
            {button.label}
          </Link>
        ))}
      <Link to={"#"}
                           className="nav-link"
                           role="button"
                           data-toggle="dropdown"
                        >
                           <div className="header-info">
								<small>Good Morning</small>
								<span>USER</span>
                           </div>
                           <img src='' width="20" alt="" />
                        </Link>
                        <div
                           className='dropdown-menu dropdown-menu-right'
                        >
                           <Link
                              to="/app-profile"
                              className="dropdown-item ai-icon"
                           >
                              <svg
                                 id="icon-user1"
                                 xmlns="http://www.w3.org/2000/svg"
                                 className="text-primary"
                                 width="18"
                                 height="18"
                                 viewBox="0 0 24 24"
                                 fill="none"
                                 stroke="currentColor"
                                 strokeWidth="2"
                                 strokeLinecap="round"
                                 strokeLinejoin="round"
                              >
                                 <path d="M20 21v-2a4 4 0 0 0-4-4H8a4 4 0 0 0-4 4v2"></path>
                                 <circle cx="12" cy="7" r="4"></circle>
                              </svg>
                              <span className="ml-2">Profile </span>
                           </Link>
                        </div>
    </Nav>
    </Navbar.Collapse>
    </Container>
    </Navbar>
  );
};

export default NavbarComponent;
