import React, { useEffect, useState } from 'react';
import { io } from 'socket.io-client';
import { useGetAccountInfo } from '@elrondnetwork/dapp-core';

const prices = [
  {
    level: 1,
    price: 100000
  },
  {
    level: 2,
    price: 500000
  },
  {
    level: 3,
    price: 1500000
  },
];

const Stable = () => {
  const { address } = useGetAccountInfo();
  const [socket, setSocket] = useState();
  const [stable, setStable] = useState([]);
  const [nextLevel, setNextLevel] = useState();
  const [nextLevelPrice, setNextLevelPrice] = useState();
  useEffect(() => {
    const s = io('http://localhost:4000');

    function handler (stable) {
      setStable(stable);
    }

    s.emit('get-stable', address);
    s.on('recive-stable', handler);
    setSocket(s);

    return () => {
      s.disconnect();
    }
  }, [])

  useEffect(() => {
    if(stable != null) {
      for(let i = 0; i < prices.length; i++) {
        if(prices[i].level == stable.level && stable.level < 3) {
          setNextLevel(prices[i+1].level);
          setNextLevelPrice(prices[i+1].price);
        }
      }
    } else {
      setNextLevel(1);
      setNextLevelPrice(prices[0].price);
    }
  }, [stable]);

  async function upgradeStable(address) {
    socket.emit('upgrade-stable', address);
  }
  return (
    <div className='container'>
      <div className="row">
        <div className="col-12">
          <div className="card">
            <div className="card-body">
              {
                stable == null ? (
                  <h1>Level #1</h1>
                ) : (
                  <h1>Level #{stable.level}</h1>
                )
              }
            </div>
            <div className="card-footer">
              <button
                className='btn btn-primary'
                onClick={() => upgradeStable(address)}
              >
                Upgrade for {nextLevelPrice / 100}eStar
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  )
}

export default Stable;
