import React, { useState, useEffect } from 'react';

const SelectNfts = ({ address, socket }) => {
  const [nft, setNft] = useState(null);
  useEffect(() => {
    const handler = (nfts) => {
      setNft(nfts);
    };

    socket.emit('get-status', address);
    socket.on('recive-status', handler);
  }, [])

  console.log(nft);

  return (
    nft.map(({ fileUri, name}) => {
                      if(stamina >= 25) {
                        return (
                          <div
                            key={name}
                            className='col-12 col-md-4 text-center'
                            onClick={() => {
                              setClickable(false);
                             enter(name, fileUri);
                            }}
                          >
                            <img src={fileUri} className='d-block mx-auto' height='250px' />
                            <p style={{ color: 'black' }}>{name}</p>
                            {props.id > 1 ? '' : stamina == 25 && <p className='text-danger'>Stamina: {stamina}</p> ||
                              stamina == 50 && <p className='text-warning'>Stamina: {stamina}</p> ||
                              stamina > 50 && <p className='text-primary'>Stamina: {stamina}</p>
                            }
                          </div>
                        );
                      }
                    })
  )
}

export default SelectNfts;
