  useEffect(handleRedirect, [address]);
  return (
    <div>
      {tracks.map((track) => (
        <div key={track.name}>
          <h2>{track.title}</h2>
          <p>{track.description}</p>
          <div>
            <span>
              Ranking prize:
              {track.ranking.map((rank) => (
                <p key={rank.name}>
                  {rank.name} {rank.win} ESTAR
                </p>
              ))}
            </span>
          </div>
          <div>{track.button}</div>
        </div>
      ))}
    </div>
  );
};

export default Race;
