import React, { useState, useEffect } from 'react'
import {
  faDoorOpen
} from '@fortawesome/free-solid-svg-icons';
import Modal from 'react-bootstrap/Modal';
import Button from 'react-bootstrap/Button';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import Enter from './Enter';

const TournamentModel = ({ address, socket }) => {
  const [show, setShow] = useState(false);
  const [loading, setLoading] = useState(true);
  const [nft, setNft] = useState(null);
  const [message, setMessage] = useState(null);

  // Loading

  function setDelay(ms) {
    return new Promise((resolve, reject) => {
      setTimeout(resolve, ms);
    })
  }

  async function makeLoading() {
    await setDelay(1000);
    setLoading(false);
  }
  useEffect(() => {
    makeLoading();
  }, []);

  useEffect(() => {
    const handler = (nfts) => {
      setNft(nfts);
    };

    socket.emit('get-status', address);
    socket.on('recive-status', handler);
  }, [])

  async function enter(horse) {
    const handler = (message) => {
      setMessage(message);
      console.log(message);
    }

    await socket.emit('enter-tournament', address, horse);
    await socket.on('recive-tournament-response', handler);
  }

  return (
    <>
      <button
        className="btn btn-primary"
        onClick={() => setShow(true)}
      >
        Join <FontAwesomeIcon icon={faDoorOpen} />
      </button>
      <Modal
        show={show}
        onHide={show}
        backdrop="static"
        size="lg"
        keyboard={false}
      >
        {message != null ? (
          <Modal.Header closeButton>
            <Modal.Title>Congratulations</Modal.Title>
        </Modal.Header>
        ) : (
          <Modal.Header>
            <Modal.Title>Select your horse</Modal.Title>
        </Modal.Header>
        )}
        <Modal.Body>
          {
            loading ? (
              <div className="d-flex justify-content-center">
                <div className="spinner-border" role="status">
                  <span className="sr-only">Loading...</span>
                </div>
              </div>
            ) : nft == null ? (
              <h3>You don't have nfts.</h3>
            ) : ( 
              <div className="row">
                {
                  message == null ? 
                    nft.map(({ fileUri, name }) => {
                      return (
                        <div
                          key={name}
                          className='col-12 col-md-4 text-center'
                          onClick={() => {
                            enter(name);
                          }}
                        >
                          <img src={fileUri} className='d-block mx-auto' height='250px' />
                          <p style={{ color: 'black' }}>{name}</p>
                        </div>
                      );
                    }) : (
                      <h1>da</h1>
                    )
                }
              </div>
            )
          }
          {message != null ? (
            <Modal.Footer>
              <Button variant="secondary" onClick={() => setShow(false)}>
                Close
              </Button>
              <Button variant="primary">Understood</Button>
            </Modal.Footer>
          ) : (
            <p></p>
          )}
        </Modal.Body>
      </Modal>
    </>
  )
}

export default TournamentModel;
