import React from 'react';
import { useState, useEffect } from 'react';
import { useGetAccountInfo } from '@elrondnetwork/dapp-core';
import styles from './styles.module.scss';
import axios from 'axios';

const Nfts = () => {
  const [nft, setNft] = useState(null);
  const { address } = useGetAccountInfo();
  const nftFetchUrl = `https://api.elrond.com/accounts/${address}/nfts?collection=EQUISTAR-3f393f`;
  const nftWalletFetch = () => {
    axios.get(nftFetchUrl).then((res) => {
      console.log(res.status);
      if (res.status == 500) {
        setNft(null);
      } else if (res.status <= 200) {
        setNft(res.data);
      }
    });
  };
  useEffect(nftWalletFetch, []);
  console.log(nft);
  if (nft) {
    return <p>hello</p>;
  } else {
    return <p>You don&apost have Equistar Nfts.</p>;
  }
};

export default Nfts;
