import React, { useState, useEffect } from 'react'
import { useNavigate } from 'react-router-dom';
import Image from 'react-bootstrap/Image';

const AccountInfo = ({ account, socket }) => {
  const navigate = useNavigate();
  const [entries, setEntries] = useState(0);
  const [win, setWin] = useState(0);

  useEffect(() => {
    if(socket == null ) return;
    socket.emit('load-account', account.address);
  }, [socket])

  useEffect(() => {
    if(socket == null) return;
    socket.on('get-account-entries', entries => setEntries(entries));
    socket.on('get-account-win', win => setWin(win));
  })

  return (
      <div className="container">
        <div className="row d-flex align-items-center">
          <div className="col-3 mx-auto text-light"> 
            <Image src='https://www.pngfind.com/pngs/m/540-5404861_horse-riding-clipart-animated-spirit-horse-riding-free.png' roundedCircle fluid />
            <span classNameborder-end></span>
          </div>
          <div className="col-4 text-light"> 
            <h1>Hello, {account.username}!</h1>
            <button
              className="btn btn-primary"
              onClick={() => navigate('/account/edit')}
            >
              Edit profile
            </button>
          </div>
          <div className="col-5 text-light">
            <h2 className='text-center'>Statistics</h2>
            <h4>Races entries: {entries}</h4>
            <h4>Win: {win}</h4>
         </div>
        </div>
      </div>
  );
}

export default AccountInfo;
