import axios from 'axios';
import { network } from '../config';

const http = axios.create({
  baseURL: network.ApiAddress,
  headers: {
    'Content-type': 'application/json'
  }
});

const collection = 'EQUISTAR-3f393f';

const fetchNfts = (props) => {
  if (props.what == 'NFT') {
    try {
      const { data } = http.get(
        '/accounts/' + props.address + '/nfts?collection=' + collection
      );
      console.log(data);
      return {
        data: data,
        success: true
      };
    } catch (err) {
      return {
        data: err.response.data,
        success: false
      };
    }
  }
};

export default ApiRequests = fetchNfts();
