import React, { useEffect, useState } from 'react';
import { useGetAccountInfo } from '@elrondnetwork/dapp-core';
import { faSpinner } from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { useNavigate } from 'react-router-dom';
import styles from './styles.module.scss';
import axios from 'axios';
import getNfts from '../../apiRequests';

const Dashboard = () => {
  const [nft, setNfts] = React.useState();
  const baseURL = 'https://api.elrond.com/nfts?collection=EQUISTAR-3f393f';

  if(nft == null) {
   React.useEffect(() => {
    axios.get(baseURL).then((response) => {
      setNfts(response.data);
    });
  }, []);
  }
  console.log(nft);
  const { address } = useGetAccountInfo();
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();
  const handleRedirect = () =>
    Boolean(address) ? setLoading(false) : navigate('/unlock');

  useEffect(handleRedirect, [address]);

  if (loading) {
    return (
      <div
        style={{ fontSize: '30px' }}
        className='d-flex align-items-center justify-content-center text-white flex-fill'
      >
        <FontAwesomeIcon
          icon={faSpinner}
          size='2x'
          spin={true}
          className='mr-3'
        />
        Loading...
      </div>
    );
  }

  return (
    <div className={styles.dashboard}>
      <h1></h1>
    </div>
  );
};

export default Dashboard;
