import React, { useState, useEffect } from 'react';
import { transactionServices, refreshAccount } from '@elrondnetwork/dapp-core';
import styles from './styles.module.scss';
import TxTracking from '../Layout/TxTracking';
import { useGetAccountInfo } from '@elrondnetwork/dapp-core';
import { getNfts } from '../../apiRequests';
import { Modal } from 'react-bootstrap';
import { io } from 'socket.io-client';

const Button = (props) => {
  const { address } = useGetAccountInfo();
  const { successfulTransactionsArray } = TxTracking();
  const [, setTransactionSessionId] = useState('');
  const [session, setSession] = useState('');
  const [paid, setPaid] = useState(false);
  const { sendTransactions } = transactionServices;
  const [estar, setEstar] = useState(0);
  const [egld, setEgld] = useState(0);
  const [nft, setNft] = useState([]);
  const [socket, setSocket] = useState();
  const [show, setShow] = useState(false);
  const [showSelect, setShowSelect] = useState(false);
  const [success, setSuccess] = useState(false);
  const [message, setMessage] = useState('');
  const [raceIsAv, setRaceIsAv] = useState(false);
  const [timeLeft, setTimeLeft] = useState(0);

  useEffect(() => {
    const fetch = async () => {
      const data = await getNfts(address).then((res) => res.data);
      setNft(data);
    };
    fetch();
    // Connect socket.io
    const s = io('http://localhost:4000');
    setSocket(s);

    // Fee
    if (props.fee > 0) {
      setEstar(props.fee * 100);
      setEgld(props.fee / 200);
    }
    // Disconnect socket.io
    return () => {
      s.disconnect();
    };
  }, [socket]);

  useEffect(() => {
    function handler (nr) {
      setTimeLeft(nr);
    }
    const time =  100;
    socket.emit('time-remaining', time);
    socket.on('recive-time-remaining', handler);
  }, [timeLeft]);

  async function available () {
    const handler = (isAvailable) => {
      setRaceIsAv(isAvailable);
    };
    await socket.emit('get-available', props.id);
    await socket.on('recive-available', handler);
    setShow(true);
  };

  useEffect(() => {
    for (let i = 0; i < successfulTransactionsArray.length; i++) {
      if (successfulTransactionsArray[i][0] == session) {
        setPaid(true);
      }
    }
  }, [successfulTransactionsArray]);

  useEffect(() => {
    if(paid) setShowSelect(true);
  }, [paid])

  const encrypt = () => {
    return (estar * 100).toString(16);
  };

  const sendEstarTransaction = async () => {
    const estarTransaction = {
      value: 0,
      gasLimit: '500000',
      data:
        estar >= 50 && estar <= 656
          ? btoa('ESDTTransfer@45535441522d616661616630@' + encrypt())
          : btoa('ESDTTransfer@45535441522d616661616630@0' + encrypt()),
      receiver: 'erd1q07w9xm8avd7kwj3cgn3xrnhzg5da7e3vg7dv6gs3npyql0jpq9ss35a20'
    };
    await refreshAccount();

    const { sessionId } = await sendTransactions({
      transactions: estarTransaction,
      transactionsDisplayInfo: {
        processingMessage: 'Processing transaction',
        errorMessage: 'An error has occured during',
        successMessage: 'Transaction successful'
      },
      redirectAfterSign: false
    });
    if (sessionId != null) {
      setTransactionSessionId(sessionId);
      setSession(sessionId);
    }
  };

  const sendEgldTransaction = async () => {
    const egldTransaction = {
      value: egld ? egld * 1000000000000000000 : 0,
      gasLimit: '70000',
      data: 'QG9r',
      receiver: 'erd1q07w9xm8avd7kwj3cgn3xrnhzg5da7e3vg7dv6gs3npyql0jpq9ss35a20'
    };
    await refreshAccount();

    const { sessionId } = await sendTransactions({
      transactions: egldTransaction,
      transactionsDisplayInfo: {
        processingMessage: 'Processing transaction',
        errorMessage: 'An error has occured during',
        successMessage: 'Transaction successful'
      },
      redirectAfterSign: true
    });
    if (sessionId != null) {
      setTransactionSessionId(sessionId);
      setSession(sessionId);
    }
  };

  const enter = async (horse) => {
    const data = await {
        address: address,
        horse: horse,
        feePaid: true,
        raceId: props.id,
      };
    socket.emit('enter-race', data);
    const test = (response) => {
        setSuccess(true);
        setShowSelect(response.showb);
        setMessage(response.message);
      }

      socket.on('recive-response', test);
  };

  return (
    <div>
      <button
        className={styles.btn}
        style={{ backgroundColor: props.color }}
        onClick={available}
      >
        {props.name} {timeLeft}
      </button>
      {/* Enter race modal */}
      <Modal
        dialogClassName='custom-dialog'
        show={show}
        onHide={() => setShow(!show)}
        aria-labelledby='contained-modal-title-vcenter'
        centered
      >
        <Modal.Header closeButton className='bg-white'>
          {raceIsAv ? (
            <Modal.Title>Pay with</Modal.Title>
          ) : (
            <Modal.Title>Slots</Modal.Title>
          )}
        </Modal.Header>

        <Modal.Body className='bg-white w-100'>
          {raceIsAv ? (
            <div className='d-flex'>
              <button
                className='d-block mx-auto btn btn-primary'
                onClick={() => {
                  setShow(false);
                  sendEstarTransaction();
                }}
              >
                {estar} eStar
              </button>
              <button
                className='d-block mx-auto btn btn-primary ml-2'
                onClick={() => {
                  sendEgldTransaction();
                  setShow(false);
                }}
              >
                {egld} eGld
              </button>
            </div>
          ) : (
            <h5>No more slots are available in this race!</h5>
          )}
        </Modal.Body>
      </Modal>
      <Modal
        show={showSelect}
        size='xl'
        onHide={() => setShowSelect(false)}
        aria-labelledby='contained-modal-title-vcenter'
        centered
      >
        <Modal.Header className='bg-white'>
          <Modal.Title id='contained-modal-title-vcenter'>
            Select Horse
          </Modal.Title>
        </Modal.Header>
        <Modal.Body className='bg-white px-0'>
          <div
            className={styles.nftGrid}
            style={{ gridTemplateColumns: 'repeat(2, 1fr)' }}
          >
            {nft ? (
                  nft.map(({ identifier, name, url }) => (
                    <div
                        key={identifier}
                        className={styles.nftContent}
                        onClick={() => enter(name)}
                    >
                        <img src={url} className={styles.img} />
                        <p
                        style={props.color ? { color: props.color } : { color: 'white' }}
                        >
                              {name}
                        </p>
                    </div>
                  ))
            ) : (
        <p>You don&apos;t have EquiStar Nfts.</p>
      )}
    </div>
        </Modal.Body>
      </Modal>
      <Modal
        show={success}
        size='xl'
        onHide={() => setSuccess(false)}
        aria-labelledby='contained-modal-title-vcenter'
        centered
      >
        <Modal.Header closeButton className='bg-white'>
          <Modal.Title id='contained-modal-title-vcenter'>
            Congratulations!
          </Modal.Title>
        </Modal.Header>
        <Modal.Body className='bg-white px-0'>
          <div
            className={styles.nftGrid}
            style={{ gridTemplateColumns: 'repeat(2, 1fr)' }}
          >
            <h4>{message == 'FULL' ? 'Ops' : message}</h4>
          </div>
        </Modal.Body>
      </Modal>
    </div>
  );
};

export default Button;
