import React from 'react';
import { useState, useEffect } from 'react';
import { useGetAccountInfo } from '@elrondnetwork/dapp-core';
import { useLocation } from 'react-router-dom';
import axios from 'axios';

import styles from './styles.module.scss';

function Track() {
  const loc = useLocation().pathname.replace('/race/track/', '');
  const { address } = useGetAccountInfo();
  const nftFetchUrl = `https://api.elrond.com/accounts/${address}/nfts?collection=EQUISTAR-3f393f`;
  const nftWalletFetch = () => fetch(nftFetchUrl).then((res) => res.json());
  const [nft, setNft] = useState([]);
  useEffect(() => {
    nftWalletFetch().then((data) => setNft(data));
  }, []);

  function checkClass(c) {
    const items = axios
      .get(`http://localhost:4000/race/class/${c}`)
      .then((data) => console.log(res.data));
    console.log(items);
    return items;
  }

  function createRaceData(e) {
    const number = Math.ceil(Math.random() * 4);
    let clasa = '';
    if (number == 1) {
      clasa = 'A';
    } else if (number == 2) {
      clasa = 'B';
    } else if (number == 3) {
      clasa = 'C';
    } else if (number == 4) {
      clasa = 'D';
    }
    checkClass(clasa);
    axios.post('http://localhost:4000/race/enter', {
      address: address,
      horse: e.name,
      track: loc,
      class: clasa,
      fee: true,
      feePaidWith: 'EGLD'
    });
  }

  return (
    <div>
      <h1 className={styles.title}>Select your horse!</h1>
      <div className={styles.nftGrid}>
        {nft ? (
          nft.map((item) => (
            <div
              key={item.identifier}
              className={styles.nftContent}
              onClick={() => createRaceData(item)}
            >
              <img src={item.url} className={styles.img} />
            </div>
          ))
        ) : (
          <p>You don&apos;t have EquiStar Nfts.</p>
        )}
      </div>
    </div>
  );
}

export default Track;
