import axios from 'axios';

const fetchNfts = (url: string) =>
  async function getNfts({
    apiAddress,
    address,
    contractAddress,
    timeout
  }) {
    try {
      const { data } = await axios.get(`${apiAddress}${url}`, {
        params: {
          sender: address,
          receiver: contractAddress,
          condition: 'must',
          size: 25
        },
        timeout
      });

      return {
        data: data,
        success: data !== undefined
      };
    } catch (err) {
      return {
        success: false
      };
    }
  };

export const getNfts = fetchNfts('/transactions');
