import React from 'react';
import { useState, useEffect } from 'react';
import { useGetAccountInfo } from '@elrondnetwork/dapp-core';
import styles from './styles.module.scss';
import axios from 'axios';

const { address } = useGetAccountInfo();
const nftFetchUrl = `https://api.elrond.com/accounts/${address}/nfts?collection=EQUISTAR-3f393f`;
const nftWalletFetch = () => {
    axios.get(nftFetchUrl).then((res) => res.data);
  };

const Nfts = () => {
  const [nft, setNft] = useState();
  useEffect(nftWalletFetch, []);
  console.log(nft);
  if (nft) {
    return <p>hello</p>;
  } else {
    return <p>You don&apos;t have Equistar Nfts.</p>;
  }
};

export default Nfts;
