import React from 'react';
import { useState, useEffect } from 'react';
import { useGetAccountInfo } from '@elrondnetwork/dapp-core';
import { useLocation, useNavigate } from 'react-router-dom';
import axios from 'axios';

import styles from './styles.module.scss';

function Track() {
  const loc = useLocation().pathname.replace('/race/track/', '');
  const navigate = useNavigate();
  const { address } = useGetAccountInfo();
  const nftFetchUrl = `https://api.elrond.com/accounts/${address}/nfts?collection=EQUISTAR-3f393f`;
  const nftWalletFetch = () => fetch(nftFetchUrl).then((res) => res.json());
  const [nft, setNft] = useState([]);
  const [slots, setSlots] = useState(0);
  const [exist, setExist] = useState(false);
  const bonus = ['SPEED', 'EDURANCE', 'AGILITY'];

  const getSlotsAvailable = () => {
    axios
      .get(`http://localhost:4000/backend/race/${loc}`)
      .then((res) => {
        if (res.data.length != 0) {
          setExist(true);
          for (let i = 0; i < res.data.length; i++) {
            const data = res.data[i].player;
            setSlots(data.length);
          }
        }
      })
      .catch((err) => console.log(err));
  };

  useEffect(() => {
    getSlotsAvailable();
    nftWalletFetch().then((data) => setNft(data));
  }, [exist]);

  function createRace(e) {
    if (slots < 8 && exist) {
      if (e != null) {
        axios.patch(`http://localhost:4000/backend/race/${loc}`, {
          player: [
            {
              address: address,
              horse: e.name,
              feePaid: true
            }
          ]
        });
        getSlotsAvailable();
      }
      console.log('Player is insered.');
    } else if (!exist) {
      axios.post('http://localhost:4000/backend/race', {
        id: loc,
        bonus: bonus[Math.floor(Math.random() * bonus.length)],
        player: [
          {
            address: address,
            horse: e.name,
            feePaid: true
          }
        ]
      });
      setExist(true);
      console.log('Created');
    } else if (slots > 7 && exist) {
      axios
        .get('http://localhost:4000/backend/races')
        .then((res) => {
          for (let i = 0; i < res.data.length; i++) {
            const data = res.data[i];
            console.log(data);
            switch (data.id) {
              case '1': {
                if (data.player.length < 8) {
                  console.log('The track one ok.');
                  break;
                }
              }
              case '2': {
                if (data.player.length < 8) {
                  console.log('The track two ok.');
                  break;
                }
              }
              case '3': {
                if (data.player.length < 8) {
                  console.log('The track third ok.');
                  break;
                }
              }
              case '4': {
                if (data.player.length < 8) {
                  console.log('The track four ok.');
                  break;
                }
              }
            }
          }
        })
        .catch((err) => console.log(err));
      console.log('Slots is too high');
    }
  }

  return (
    <div>
      <h1 className={styles.title}>Select your horse! {slots}</h1>
      <div className={styles.nftGrid}>
        {nft ? (
          nft.map((item) => (
            <div
              key={item.identifier}
              className={styles.nftContent}
              onClick={() => createRace(item)}
            >
              <img src={item.url} className={styles.img} />
            </div>
          ))
        ) : (
          <p>You don&apos;t have EquiStar Nfts.</p>
        )}
      </div>
    </div>
  );
}

export default Track;
