import React from 'react'
import styles from './styles.module.scss';
import { Line } from 'react-chartjs-2';
import { Chart as ChartJS } from 'chart.js/auto';

const GameplayOverview = () => {

  const data = [
    {
      day: '11.02.2020',
      distribution: '2000',
    },
    {
      day: '12.02.2020',
      distribution: '3000',
    },
    {
      day: '21.02.2020',
      distribution: '20',
    },
    {
      day: '22.02.2020',
      distribution: '230',
    },
    {
      day: '23.02.2020',
      distribution: '22220',
    },
  ];

  const data2 = {
    labels: data.map((data) => data.day),
    datasets: [{
      label: 'Distribution',
      data: data.map((data) => data.distribution),
      fill: true,
      backgroundColor: 'red',
      borderColor: 'white',
      tension: 0.1,
    }]
  }
  return (
    <>
      <div className="row">
        <div className="col-12">
          <h2 className={styles.gameplayOverviewTitle}>EquiStar Gameplay Overview</h2>
        </div>
      </div>
      <div className="row mb-2">
        <div className="col-md-4">
        <h3 className={styles.gameplayOverviewSubTitle}>Daily Races</h3>
        <div className={styles.gameplayGraph}>
          <Line data={data2} options={{ responsive:true }} style={{ height: '100%' }} />
        </div>
      </div>
      <div className="col-md-8">
        <h3 className={styles.gameplayOverviewSubTitle}>ESTAR Daily Distribution</h3>
        <div className={styles.gameplayGraph}></div>
      </div>
      </div>
    </>
  )
}

export default GameplayOverview;
