import React, { useState, useEffect } from 'react';
import { getNftsWallet } from '../../apiRequests';
import { useGetAccountInfo } from '@elrondnetwork/dapp-core';
import styles from './styles.module.scss';

const SelectHorse = (props: any) => {
  const { address } = useGetAccountInfo();
  const [nft, setNft] = useState([]);
  useEffect(() => {
    const fetch = async () => {
      const data = await getNftsWallet(address).then((res) => res.data);
      setNft(data);
      console.log(data);
    };
    fetch();
  }, []);
  return (
    <div className={styles.nftGrid}>
      {nft ? (
        nft.map(({ identifier, name, url }) => (
          <div
            key={identifier}
            className={styles.nftContent}
            onClick={() => console.log('1')}
          >
            <img src={url} className={styles.img} />
          </div>
        ))
      ) : (
        <p>You don&apos;t have EquiStar Nfts.</p>
      )}
    </div>
  );
};

export default SelectHorse;
