import React from 'react';
import { Modal } from 'react-bootstrap';

const PayModal = ({ race, show }) => {
  console.log(race);
  return (
    <Modal
      dialogClassName='custom-dialog'
      show={show}
      onHide={() => setShow(!show)}
      aria-labelledby='contained-modal-title-vcenter'
      centered
    >
      <Modal.Header closeButton className='bg-white'>
        <Modal.Title>Pay</Modal.Title>
      </Modal.Header>
      <Modal.Body className='bg-white w-100'>
        {raceIsAv ? (
          <>
            {race.entryFee != 0 ? (
              race.withEstar ? (
               <button
                  className='d-block mx-auto btn btn-primary'
                  onClick={() => {
                    setShow(false);
                    sendEstarTransaction();
                  }}
                >
                  {estar} {tokenName}
               </button>
              ) : (
                <button
                  className='d-block mx-auto btn btn-primary'
                  onClick={() => {
                    setShow(false);
                    sendEgldTransaction();
                  }}
                >
                  {egld} EGLD
                </button>
              )
              ) : (
                <button
                  className='d-block mx-auto btn btn-primary'
                  onClick={() => {
                    setShow(false);
                    sendEgldTransaction();
                  }}
                >
                  Pay
                </button>
              )}
            </>
          ) : (
            <h5>{message}</h5>
          )}
      </Modal.Body>
    </Modal>
  )
}

export default PayModal;
