import * as React from 'react';
import { useState, useEffect } from 'react';
import { io } from 'socket.io-client';

import { Navbar, Container, Nav } from 'react-bootstrap';

import { logout, useGetAccountInfo } from '@elrondnetwork/dapp-core';
import { Link, useNavigate } from 'react-router-dom';
import Logo from 'assets/Logo';

import modifiable from 'helpers/modifiable';
import styles from './styles.module.scss';
import { backend } from 'config';

const NavbarComponent = () => {
  const [socket, setSocket] = useState(null);
  const [account, setAccount] = useState('');
  let navigate = useNavigate();
  const routeChange = (path) => {
    navigate(path);
  };
  const { address } = useGetAccountInfo();

  useEffect(() => {
    const s = io(backend);
    setSocket(s);

    return () => {
      s.disconnect();
    }
  }, [])

  useEffect(() => {
    if(socket === null) return;
      else {
        socket.emit('load-account', address)
        socket.on('get-account', (data) => {
          if(data.message === 'OK') {
            setAccount(data.account.username)
          } else {
            setAccount(address.substr(0, 8) + '...' + address.substr(address.length - 8))
          }
        })
      }
  }, [socket])

  const buttons = [
    {
      label: account,
      onClick: () => routeChange('/account')
    },
    {
      label: 'Race',
      onClick: () => routeChange('/race')
    },
    {
      label: 'History',
      onClick: () => routeChange('/raceHistory')
    },
    {
      label: 'Category',
      onClick: () => routeChange('/category')
    },
    {
      label: 'Disconnect',
      onClick: () => logout('/unlock')
    }
  ];

  return (
    <Navbar expand="md">
      <Container fluid>
      <Navbar.Brand>
        <Link to='/' className={styles.heading}>
          <Logo width={50} height={50} />
          <span className={styles.title + ' ml-3'}>Estar</span>
      </Link>
      </Navbar.Brand>
      <Navbar.Toggle aria-controls="navbarScroll" className='bg-white' />
      <Navbar.Collapse id="navbarScroll" className="justify-content-end">

      <Nav
        className="me-auto my-2 my-lg-0"
        style={{ maxHeight: '100px' }}
        navbarScroll
      >
      {buttons.map((button) => (
          <Nav.Link key={button.label}>
            <p>{button.label}</p>
          </Nav.Link>
        ))}
    </Nav>
    </Navbar.Collapse>
    </Container>
    </Navbar>
  );
};

export default NavbarComponent;
