import React, { useState, useEffect } from 'react';
import { transactionServices ,sendTransactions, refreshAccount } from '@elrondnetwork/dapp-core';

const Enter = ({ address, socket }) => {
  const [loading, setLoading] = useState(true);
  const [transactionSessionId, setTransactionSessionId] = useState('');
  const txSuccess = transactionServices.useGetSuccessfulTransactions();
  const [nft, setNft] = useState(null);
  const [message, setMessage] = useState('');

  // Loading

  function setDelay(ms) {
    return new Promise((resolve, reject) => {
      setTimeout(resolve, ms);
    })
  }

  async function makeLoading() {
    await setDelay(1000);
    setLoading(false);
  }
  useEffect(() => {
    makeLoading();
  }, []);

  useEffect(() => {
    const handler = (nfts) => {
      setNft(nfts);
    };

    socket.emit('get-status', address);
    socket.on('recive-status', handler);
  }, [])

  useEffect(() => {
    for(let i = 0; i < txSuccess.successfulTransactionsArray.length; i++) {
      if(txSuccess.successfulTransactionsArray[i][0] == transactionSessionId) {
        setPaid(true);
      }
    }
  }, [txSuccess.successfulTransactionsArray.length])

  useEffect(() => {
    if(paid) enter(address);
  }, [paid]);

  function numHex(s) {
    var a = s.toString(16);
    if ((a.length % 2) > 0) {
        a = "0" + a;
    }
    return a;
  }

   const sendTransaction = async () => {
    const estarTransaction = {
      value: 0,
      gasLimit: '500000',
      data: btoa('ESDTTransfer@45535441522d616661616630@' + numHex()),
      receiver: 'erd1q07w9xm8avd7kwj3cgn3xrnhzg5da7e3vg7dv6gs3npyql0jpq9ss35a20'
    };
    await refreshAccount();

    const { sessionId } = await sendTransactions({
      transactions: estarTransaction,
      transactionsDisplayInfo: {
        processingMessage: 'Processing transaction',
        errorMessage: 'An error has occured during',
        successMessage: 'Transaction successful'
      },
      redirectAfterSign: false
    });
    if (sessionId != null) {
      setTransactionSessionId(sessionId);
    }
  };

  async function enter(horse) {
    const handler = (message) => {
      setMessage(message);
    }

    await socket.emit('enter-tournament', address, horse);
    await socket.on('recive-tournament-response', handler);
  }

  if(loading) {
    return (
      <div className="d-flex justify-content-center">
        <div className="spinner-border" role="status">
          <span className="sr-only">Loading...</span>
        </div>
      </div>
    );
  } else {
    if(nft == null) {
      return <h3>You don't have nfts.</h3>;
    } else {
        return (
          <div className="row">
            {
              message == '' ? 
              nft.map(({ fileUri, name }) => {
                return (
                  <div
                    key={name}
                    className='col-12 col-md-4 text-center'
                    onClick={() => {
                      enter(name);
                    }}
                  >
                    <img src={fileUri} className='d-block mx-auto' height='250px' />
                    <p style={{ color: 'black' }}>{name}</p>
                  </div>
                );
              }) : (
                <h4 className='p-3'>{message}</h4>
              )

            }
          </div>
        );
    }
  }
}

export default Enter;
