import React, { useEffect, useState } from 'react';
import { io } from 'socket.io-client';
import { useGetAccountInfo, sendTransactions, refreshAccount } from '@elrondnetwork/dapp-core';
import TxTracking from '../../components/Layout/TxTracking';

const Stable = () => {
  const { address } = useGetAccountInfo();
  const [wait, setWait] = useState(false);
  const [paid, setPaid] = useState(false);
  const { successfulTransactionsArray } = TxTracking();
  const [socket, setSocket] = useState();
  const [stable, setStable] = useState([]);
  const [currentLevel, setCurrentLevel] = useState();
  const [nextLevel, setNextLevel] = useState();
  const [nextLevelPrice, setNextLevelPrice] = useState();
  const [nextLevelPriceHash, setNextLevelPriceHash] = useState('');
  const [, setTransactionSessionId] = useState('');
  const [session, setSession] = useState('');
  useEffect(() => {
    const s = io('http://localhost:4000');

    function handler (data) {
      setCurrentLevel(data.curLevel);
      setNextLevel();
      setNextLevelPrice();
      setNextLevelPriceHash();
    }

    s.emit('get-stable', address);
    s.on('recive-stable', handler);
    setSocket(s);

    return () => {
      s.disconnect();
    }
  }, [])

  useEffect(() => {
    for (let i = 0; i < successfulTransactionsArray.length; i++) {
      if (successfulTransactionsArray[i][0] == session) {
        setPaid(true);
      }
    }
  }, [successfulTransactionsArray]);

  useEffect(() => {
    if(paid) {
      upgradeStable(address)
    }
  }, [paid])

  function setDelay(ms) {
    return new Promise((resolve, reject) => {
      setTimeout(resolve, ms);
    })
  };

  async function upgradeStable(address) {
    function handler (stable2) {
      setStable(stable2);
      console.log('2');
    }

    socket.emit('upgrade-stable', address);
    await setDelay(2000);
    socket.on('up-recive-stable', handler);
    setWait(false);
  }

  const sendTransaction = async () => {
    setWait(true);
    const transaction = {
      value: 0,
      gasLimit: '500000',
      data: btoa('ESDTTransfer@45535441522d616661616630@' + nextLevelPriceHash),
      receiver: 'erd1q07w9xm8avd7kwj3cgn3xrnhzg5da7e3vg7dv6gs3npyql0jpq9ss35a20'
    };
    await refreshAccount();

    const { sessionId } = await sendTransactions({
      transactions: transaction,
      transactionsDisplayInfo: {
        processingMessage: 'Processing transaction',
        errorMessage: 'An error has occured during',
        successMessage: 'Transaction successful'
      },
      redirectAfterSign: true
    });
    if (sessionId != null) {
      setTransactionSessionId(sessionId);
      setSession(sessionId);
    }
  };

  return (
    <div className='container'>
      <div className="row">
        <div className="col-12">
          <div className="card">
            <div className="card-body">
              <h1>Level #{currentLevel}</h1>
            </div>
            <div className="card-footer">
              {wait ? (
                <button
                  className='btn btn-warning'
                >
                  Loading...
                </button>
              ) : nextLevel <= 3 ? (
                <button
                  className='btn btn-primary'
                  onClick={() => sendTransaction()}
                >
                  Upgrade to level #{nextLevel} for {nextLevelPrice / 100} eStar
                </button>
              ) : (
                <button disabled className='btn btn-danger d-block mx-auto'>
                  <strong>Max level</strong>
                </button>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  )
}

export default Stable;
