import React, { useEffect, useState } from 'react'
import {
  faDoorOpen
} from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';

const Card = ({ active, tournament }) => {
  const [loading, setLoading] = useState(true);

  // Tournament stats
  const [numberOfPlayers, setNumberOfPlayers] = useState(0);
  const [maxPlayers, setMaxPlayers] = useState(0);
  const [date, setDate] = useState('');

  function setDelay(ms) {
    return new Promise((resolve, reject) => {
      setTimeout(resolve, ms);
    })
  }

  async function makeLoading() {
    await setDelay(1000);
    setLoading(false);
  }
  useEffect(() => {
    makeLoading();
  }, []);

  // Tournament status

  useEffect(() => {
    if(tournament != null) {
      setMaxPlayers(tournament.maxPlayers)
      setNumberOfPlayers(tournament.players.length);
      setDate(tournament.date);
    };
  }, [tournament])

  if(loading) {
    return (
      <div className="d-flex justify-content-center text-white">
        <div className="spinner-border" role="status">
          <span className="sr-only">Loading...</span>
        </div>
      </div>
    );
  } else {
    if(active) {
      return (
        <div className="card text-center">
          <div className="card-header">
            Tournament
          </div>
          <div className="card-body">
            <h5 className="card-title">
              <strong>The special tournament is ready!</strong>
            </h5>
            <p className="card-text">Players: {numberOfPlayers}</p>
            <p className="card-text">Max players: {maxPlayers}</p>
            <button
              className="btn btn-primary">
              Join <FontAwesomeIcon icon={faDoorOpen} />
            </button>
          </div>
          <div className="card-footer text-muted">
            End date: {date}
          </div>
        </div>
      )
    } else {
      return (
        <div className="card text-center">
          <div className="card-header">
            Tournament
          </div>
          <div className="card-body">
            <h5 className="card-title">
              <strong>There are no active tournaments!</strong>
            </h5>
            <p className="card-text">With supporting text below as a natural lead-in to additional content.</p>
          </div>
          <div className="card-footer text-muted">
            Soon
          </div>
        </div>
      )
    }
  }
}

export default Card;
