import React from 'react';
import { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useGetAccountInfo } from '@elrondnetwork/dapp-core';
import styles from './styles.module.scss';
import tracks from '../../components/Tracks';
import Button from '../../components/Button';

const Race = () => {
  const { address } = useGetAccountInfo();
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();
  const handleRedirect = () =>
    Boolean(address) ? setLoading(false) : navigate('/unlock');

  useEffect(handleRedirect, [address]);
  return (
    <div className='container'>
      <div className='row'>
        {tracks.map((track) => (
          <div key={track.id} className='col-12 col-md-4 my-2'>
            <div className='card bg-light'>
              <div className='card-header'>Track #{track.id}</div>
              <div className="card-body">
                <h2>{track.title}</h2>
                <p>{track.description}</p>
                <div>
                  <span>
                    Ranking prize:
                    {track.ranking.map((rank) => (
                      <p key={rank.name}>
                        {rank.name} {rank.win} ESTAR
                      </p>
                    ))}
                  </span>
                  {track.entryFee == 0 ? (
                    <span>Entry fee: Free</span>
                  ) : (
                    <span>Entry fee: {track.entryFee}</span>
                  )}
                </div>
              </div>
              <div className='card-footer'>
                <Button
                  className='d-block mx-auto'
                  id={track.id}
                  color={track.color}
                  name={`Track #${track.id}`}
                  fee={track.entryFee}
                />
              </div>
            </div>
          </div>
        ))};
      </div>
    </div>
  );
};

export default Race;
