import React, { useState, useEffect } from 'react'
import { useNavigate, useLocation } from 'react-router-dom';
import { useGetAccountInfo } from '@elrondnetwork/dapp-core';
import { io } from 'socket.io-client';

const RaceView = () => {
  const { address } = useGetAccountInfo();
  const { pathname, hash } = useLocation();
  const path = pathname.replace('/race/', '');
  const id = path + hash;
  const [loading, setLoading] = useState(true);
  const [status, setStatus] = useState(true);
  const [exist, setExist] = useState(false);
  const [race, setRace] = useState([]);
  const navigate = useNavigate();
  const handleRedirect = () =>
    Boolean(address) ? setLoading(false) : navigate('/unlock');
    Boolean(exist) ? setLoading(false) : navigate('/unlock');

  useEffect(() => {
    const s = io('http://localhost:4000');
    s.emit('get-race', id);
    const handler = (race, status) => {
      if(race != null) {
        setRace(race);
        setStatus(status);
        setExist(true)
      }
    }
    s.on('recive-race', handler);
    return () => {
      s.disconnect();
    }
  }, []);
  useEffect(handleRedirect, [address, exist]);
      return (
            <div>
                 <h1>Salut</h1> 
            </div>
      )
}

export default RaceView;
